import * as React from 'react'
import styled from 'styled-components'

interface IIconProps {
    color?: string,
    notification?: boolean
}

interface ISpriteProps {
    icon: string,
    color?: string,
    notification?: boolean
}
class Sprite extends React.Component<ISpriteProps, {}> {
    public render() {

        const color = this.props.color || '#ACACAC'


        const icons = {
            'profile': Profile,
            'pin': Pin,
            'cross': Cross,
            'arrow-right': ArrowRight,
            'arrow-left': ArrowLeft,
            'tick': Tick,
            'share': Share,
            'message': Message,
            'settings': Settings,
            'edit': Edit,
            'heart': Heart,
            'people': People,
            'warning': Warning,
            'cutlery': Cutlery
        }
        const ActiveIcon = icons[this.props.icon]


        return (
            <ActiveIcon color={color} notification={this.props.notification || false} />
        )
    }
}

export default Sprite



